	'Automatic &lt;P&gt; and &lt;BR&gt; tags' => "Tags &lt;P&gt; y &lt;BR&gt; automáticas",
	'BB formatting e-g [b]bold[/b]' => "Formateo BB e-g [b]bold[/b]",
	'GreyMatter style formatting' => "Formato al estilo GreyMatter",
	'**bold** \\italics\\ //italics// __underline__ ##tt## %%codeblock%%' => "**bold** \\italics\\ //italics// __underline__ ##tt## %%codeblock%%",
	'Convert text smilies to icons' => "Convierte los textos de caritas en imágenes",
	'Humane Web Text Generator 2.0 beta' => "Humane Web Text Generator 2.0 beta",
