	'Are you sure you want to delete your existing %s tables?\nDo you have a backup?' => "Etes vous sûr de vouloir supprimer vos tables %s existantes?\nAvez vous une sauvegarde?",
	'Reset done!' => "Remise a zéro effectuée!",
	'Back to menu' => "Retour au menu",
	'Language / Locale' => "Langue / Locale",
	'Choose a default language/locale for your b2evo installation.' => "Choisissez une langue/locale par défaut pour votre installation de b2evo.",
	'Online resources' => "Ressources en ligne",
