(function(){tinymce.create('tinymce.plugins.MorePlugin',{init:function(ed,url){var pb='<img src="'+url+'/img/trans.gif" class="mceMore mceItemNoResize" />',cls='mceMore',sep=ed.getParam('More_separator','<!--more-->'),pbRE;pbRE=new RegExp(sep.replace(/[\?\.\*\[\]\(\)\{\}\+\^\$\:]/g,function(a){return'\\'+a}),'g');ed.addCommand('mceMore',function(){ed.execCommand('mceInsertContent',0,pb)});ed.addButton('morebtn',{title:'moreseparator.desc',cmd:cls,image:url+'/img/morebtn.gif'});ed.onInit.add(function(){if(ed.settings.content_css!==false)ed.dom.loadCSS(url+"/css/content.css");if(ed.theme.onResolveName){ed.theme.onResolveName.add(function(th,o){if(o.node.nodeName=='IMG'&&ed.dom.hasClass(o.node,cls))o.name='more'})}});ed.onClick.add(function(ed,e){e=e.target;if(e.nodeName==='IMG'&&ed.dom.hasClass(e,cls))ed.selection.select(e)});ed.onNodeChange.add(function(ed,cm,n){cm.setActive('more',n.nodeName==='IMG'&&ed.dom.hasClass(n,cls))});ed.onBeforeSetContent.add(function(ed,o){o.content=o.content.replace(pbRE,pb)});ed.onPostProcess.add(function(ed,o){if(o.get)o.content=o.content.replace(/<img[^>]+>/g,function(im){if(im.indexOf('class="mceMore')!==-1)im=sep;return im})})},createControl:function(n,cm){return null},getInfo:function(){return{longname:'More',author:'Francois Planque',authorurl:'http://fplanque.com',infourl:'http://b2evolution.net',version:'0.1'}}});tinymce.PluginManager.add('more',tinymce.plugins.MorePlugin)})();